import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./Calendar.css"; // Import custom CSS file

function CalendarComponent() {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [fullName, setFullName] = useState("");

  const handleRangeSelection = (startAndEndDates) => {
    setStartDate(startAndEndDates[0]);
    setEndDate(startAndEndDates[1]);
  };

  // Function to log start and end dates
  // const logStartEndDates = () => {
  //   if (startDate && endDate) {
  //     const rangeLog = {
  //       startDate: startDate,
  //       endDate: endDate,
  //     };
  //     console.log(JSON.stringify(rangeLog, null, 2));
  //   } else {
  //     console.log("Please select both start and end dates.");
  //   }
  // };

  const logDates = () => {
    if (startDate && endDate) {
        const rangeLog = {
            startDate: startDate.toISOString(),
            endDate: endDate.toISOString(),
        };
        fetch('http://localhost:5000/api/dates', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(rangeLog)
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            // Handle successful response
            console.log(data);
        })
        .catch(error => {
            // Handle fetch error
            console.error('Error:', error);
            // Provide feedback to the user in the UI
            alert('An error occurred. Please try again later.');
        });
    } else {
        // Provide feedback to the user in the UI
        console.log("Please select both start and end dates.");
        alert('Please select both start and end dates.');
    }
};

  return (
    <div className="calendar-container">
    <h2 className="calendar-header">Calendar</h2>
    <div className="calendar-wrapper">
      {/* Pass selected dates and range selection handler to the Calendar component */}
      <Calendar
        className="custom-calendar"
        selectRange={true} // Enable range selection
        onChange={handleRangeSelection} // Handle range selection
      />
      {/* Second section for non-calendar elements */}
      <div className="middle-section">
        {/* Include your non-calendar elements here */}
        <button className="button" onClick={logDates}>
          Log Period
        </button>
      </div>
    </div>
  </div>
  );
}

export default CalendarComponent;
